<?php

namespace ContabilidadBundle\Repository;

/**
 * ClienteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ClienteRepository extends \Doctrine\ORM\EntityRepository
{
    public function buscarCliente($opciones=array(
                'nombre'=>'',
                'ruc'=>''
            ))
    {
        $em=$this->getEntityManager();

        $query=$em->createQueryBuilder()
            ->select('c')
            ->from('ContabilidadBundle:Cliente', 'c')
            ->Where("c.ruc like :ruc")
            ->andWhere("CONCAT(c.nombres, ' ', c.ape1, ' ', c.ape2) like :nombres")
            ->setParameter('ruc', '%'.$opciones['ruc'].'%')
            ->setParameter('nombres', '%'.$opciones['nombre'].'%')
            ->orderBy('c.nombres', 'ASC')
            ->getQuery();

        $clientes=$query->getResult();
        return $clientes;
    }

    public function tieneCedula($ruc){
        //tiene cÃ©dula anverso
        if(file_exists('cedulas/'.$ruc.'cianv.jpeg')){
            $cianv='cedulas/'.$ruc.'cianv.jpeg';
        } else {
            $cianv='cedulas/sincedula.jpeg';
        }

        if(file_exists('cedulas/'.$ruc.'cirev.jpeg')){
            $cirev='cedulas/'.$ruc.'cirev.jpeg';
        } else {
            $cirev='cedulas/sincedula.jpeg';
        }

        return array('cianv'=>$cianv, 'cirev'=>$cirev);
    }
}
